// src/components/InputPage/InputPage.js
import React, { useState, useEffect } from "react";

const InputPage = () => {
  const [link, setLink] = useState("");

  useEffect(() => {
    // Fetch the current link from the backend
    const fetchSettings = async () => {
      try {
        const response = await fetch("http://localhost:3002/api/settings");
        const data = await response.json();
        setLink(data.redirectLink || "");
      } catch (error) {
        console.error("Failed to fetch settings", error);
      }
    };

    fetchSettings();
  }, []);

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch("http://localhost:3002/api/settings", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ redirectLink: link }),
      });
      alert("Redirect link updated successfully");
    } catch (error) {
      console.error("Failed to update redirect link", error);
    }
  };

  return (
    <div className="input-page">
      <h1>Enter Redirect Link</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="link">Link:</label>
          <input
            type="text"
            id="link"
            value={link}
            onChange={handleLinkChange}
            placeholder="Enter URL here"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default InputPage;
