// src/components/InputPage/InputPage.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const InputPage = () => {
  const [link, setLink] = useState("");
  const [lastLink, setLastLink] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Retrieve the last link from the server
    const fetchLastLink = async () => {
      try {
        const response = await fetch("http://localhost:3002/api/settings");
        if (response.ok) {
          const data = await response.json();
          const savedLink = data.redirectLink || "";
          setLastLink(savedLink);
          setLink(savedLink);
        } else {
          console.error("Failed to fetch the last link");
        }
      } catch (error) {
        console.error("Error fetching the last link:", error);
      }
    };

    fetchLastLink();
  }, []);

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch("http://localhost:3002/api/settings", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ redirectLink: link }),
      });
      navigate(link); // Redirect to the new link
    } catch (error) {
      console.error("Error updating redirect link:", error);
    }
  };

  return (
    <div className="input-page">
      <h1>Enter Redirect Link</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="link">Link:</label>
          <input
            type="text"
            id="link"
            value={link}
            onChange={handleLinkChange}
            placeholder="Enter URL here"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default InputPage;
