// src/components/InputPage/InputPage.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
// import "./InputPage.scss";

const InputPage = () => {
  const [link, setLink] = useState("");
  const [lastLink, setLastLink] = useState("");

  useEffect(() => {
    // Retrieve the last link from local storage or set a default value
    const savedLink = localStorage.getItem("lastLink") || "";
    setLastLink(savedLink);
    setLink(savedLink);
  }, []);

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem("lastLink", link);
    // Redirect to the link specified in the text field
    window.location.href = link;
  };

  return (
    <div className="input-page">
      <h1>Enter Redirect Link</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="link">Link:</label>
          <input
            type="text"
            id="link"
            value={link}
            onChange={handleLinkChange}
            placeholder="Enter URL here"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default InputPage;
